import { Button, ButtonGroup } from 'react-bootstrap';
import { ethers } from "ethers";
import { useState } from "react";
import MinerContract from "./artifacts/contracts/MinerContract.sol/MinerContract.json";
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'sf-font';
import { TASK_COMPILE_SOLIDITY_LOG_RUN_COMPILER_END } from 'hardhat/builtin-tasks/task-names';

const contractAddress = "0x1BbE23f1fE058769Af9132AeEf021905fdBDdE6f";

function App() {

  //property variables
  const [message, setEggs] = useState("");
  const [currentMiners, setMiners] = useState("");
  const [userAddress, setAddress] = useState("Please Connect Your Account");



  //request access to metamask accounts
  async function requestAccount() {
    await window.ethereum.request({ method: 'eth_requestAccounts' });
    const provider = new ethers.providers.Web3Provider(window.ethereum);
    const signer = provider.getSigner();
    const signerAddress = await signer.getAddress();
    setAddress(signerAddress);

  }

  //helper functions

  //fetch current values stored in contract

  async function fetchMiners() {
    if (typeof window.ethereum !== "undefined") {
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const contract = new ethers.Contract(contractAddress, MinerContract.abi, provider)
      const signer = provider.getSigner();
      const signerAddress = await signer.getAddress();

      try {
        const data = await contract.getMyMiners(signerAddress);
        const newData = Number(data);
        console.log("data: ", newData);
        setMiners(newData);
        setAddress(signerAddress);
      } catch (error) {
        console.log('Error: ', error);
      }
    }
  }


  async function buyMiners() {
    if (message !== "") {
      if (typeof window.ethereum !== "undefined") {
        await requestAccount();
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        const signer = provider.getSigner();
        const signerAddress = signer.getAddress();

        const contract = new ethers.Contract(contractAddress, MinerContract.abi, signer);
        const newAmount = ethers.utils.parseUnits(message, 18);
        const transaction = await contract.buyEggs(signerAddress, newAmount);

        setEggs("");

        //waits for transaction to finish and logs new minertotal

        await transaction.wait();
        fetchMiners();
        console.log(transaction);
        /*print total miners
        const miners = await contract.getMyMiners(signerAddress);
        const newMiners = Number(miners);
        console.log(newMiners);
        */
      }
    }
  }

  return (
    <div className="minerapp">
      <nav class="navbar navbarfont navbar-expand-md navbar-dark bg-transparent mb-4">
        <div class="container-fluid" style={{ fontFamily: "SF Pro Display" }}>
          <a class="navbar-brand px-5" style={{ fontWeight: "800", fontSize: '25px' }} href="#"></a><img src="galaxylogo.png" width="8%" />
          <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarCollapse" aria-controls="navbarCollapse" aria-expanded="false" aria-label="Toggle navigation">
            <span class="navbar-toggler-icon"></span>
          </button>
          <div class="collapse navbar-collapse" id="navbarCollapse">
            <ul class="navbar-nav me-auto mb-2 px-3 mb-md-0" style={{ fontSize: "25px" }}>
              <li class="nav-item">
                <a class="nav-link active" aria-current="page" href="#">Dashboard</a>
              </li>
              <li class="nav-item">
                <a class="nav-link" href="#">List NFTs</a>
              </li>
              <li class="nav-item">
                <a class="nav-link">Bridge NFTs</a>
              </li>
            </ul>
          </div>
        </div>
        <div className='px-5'>
          <input id="connectbtn" type="button" className="connectbutton" style={{ fontFamily: "SF Pro Display" }} value="Connect Your Wallet" />
        </div>
      </nav>
      <div className='container'>
        <div className='col'>
          <body className='minerform'>
        <form>
          <div className="row pt-3">
            <div>
              <h1 className="pt-2" style={{ fontWeight: "30" }}>NFT Minter</h1>
            </div>
            <h3>/1000</h3>
            <h6>Your Wallet Address</h6>
            <div className="pb-3" id='wallet-address' style={{
              color: "#39FF14",
              fontWeight: "400",
              textShadow: "1px 1px 1px black",
            }}>
              <label>{userAddress}</label>
            </div>
          </div>
          <div>
            <label style={{ fontWeight: "300", fontSize: "18px" }}>Select NFT Quantity</label>
          </div>
          <ButtonGroup size="lg"
            aria-label="First group"
            name="amount"
            style={{ boxShadow: "1px 1px 5px #000000" }}
          >
            <Button value="1">1</Button>
            <Button value="2">2</Button>
            <Button value="3">3</Button>
            <Button value="4">4</Button>
            <Button value="5">5</Button>
          </ButtonGroup>
          <h6 className="pt-2" style={{ fontFamily: "SF Pro Display", fontWeight: "300", fontSize: "18px" }}>Buy with your preferred crypto!</h6>
          <div className="row px-2 pb-2 row-style">
            <div className="col ">
              <Button className="button-style" style={{ border: "0.2px", borderRadius: "14px", boxShadow: "1px 1px 5px #000000" }}>
                <img src={"n2dr-logo.png"} width="100%" />
              </Button>
            </div>
            <div className="col">
              <Button className="button-style" style={{ border: "0.2px", borderRadius: "14px", boxShadow: "1px 1px 5px #000000" }}>
                <img src="usdt.png" width="70%" />
              </Button>
            </div>
            <div className="col">
              <Button className="button-style" style={{ border: "0.2px", borderRadius: "14px", boxShadow: "1px 1px 5px #000000" }}>
                <img src="matic.png" width="70%" />
              </Button>
            </div>
            <div>
              <div id='txout' style={{ color: "#39FF14", marginTop: "5px", fontSize: '20px', fontWeight: '500', textShadow: "1px 1px 2px #000000" }}>
                <p style={{ fontSize: "20px" }}>Transfer Status</p>
              </div>
            </div>
          </div>
        </form> 
      </body>
      </div>
      </div>
    </div>
  )
}

export default App;
