import logo from './logo.svg';
import {ethers} from "ethers";
import {useState} from "react";
import MinerContract from "./artifacts/contracts/MinerContract.sol/MinerContract.json";
import './App.css';

const contractAddress = "0x6396D1cC25971400A1c572B97385343426c51540";

function App() {

  //property variables
  const [message, buyEggs] = useState("");

  //request access to metamask accounts
  async function requestAccount(){
    await window.ethereum.request({method: 'eth_requestAccounts'});
  }

  //helper functions

  //fetch current values stored in contract

  async function fetchMiner() {
    if (typeof window.ethereum !== "undefined"){
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const contract = new ethers.Contract(contractAddress, MinerContract.abi, provider)

      try {
        const data = await contract.getMyMiners()
        console.log("data: ", data)
      } catch (error) {
        console.log('Error: ', error);
      }
    }
  }

  return(

    <div className = "App">
      <div  className = "App-header">

      <input/>
      <button>Buy Miners</button>
      <button>Sell Meteorite</button>

     </div>
    </div>
  )
}

export default App;
