class Main extends Component {
  render(){
    return (
    <div className = "App">
        <header className = "App-header">
          <h1>Galaxy Fuel Miner</h1>
        </header>
            <Alert>Your Miners: {currentMiners}</Alert>
            <Alert>Current Address: {userAddress}</Alert>

            <input
            onChange={(e) => setEggs(e.target.value)}

            value ={message}

            placeholder='convert GFC to miners'/>

          <Button onClick={ buyMiners}>Buy Miners</Button>
          <Button onClick={fetchMiners}>Sell Meteorite</Button>
          <Button onClick ={requestAccount}> connect wallet</Button>
  </div>
    )
  }
}

export default Main;
